import React, { useState } from 'react';
import { FiPhone, FiSettings, FiList } from 'react-icons/fi';
import Navbar from '../components/Navbar';
import CallLog from './components/CallLog';
import Settings from './components/Settings';
import NewUI from './components/NewUI';

const HomePage = () => {
  const [activeComponent, setActiveComponent] = useState('welcome');
  const [darkMode, setDarkMode] = useState(false);
  const username = 'Keeerthy Suresh';
  const userStatus = 'active';

  const renderComponent = () => {
    switch (activeComponent) {
      case 'call':
        return <NewUI darkMode={darkMode} />;
      case 'call-log':
        return <CallLog darkMode={darkMode} />;
      case 'settings':
        return <Settings darkMode={darkMode} />;
      default:
        return (
          <div className="flex flex-col items-center justify-center h-full space-y-6 animate-fade-in">
            <h1 className="text-4xl font-bold text-gradient bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-purple-600">
              Welcome to Echo Link
            </h1>
            <p className="text-lg text-gray-600 dark:text-gray-300">
              Click an icon to view a section.
            </p>
          </div>
        );
    }
  };

  const icons = [
    { key: 'call', label: 'Call', icon: FiPhone },
    { key: 'call-log', label: 'Call Log', icon: FiList },
    { key: 'settings', label: 'Settings', icon: FiSettings },
  ];

  const handleLogout = () => {
    window.location.href = '/';
  };

  return (
    <div
      className={`min-h-screen flex flex-col ${
        darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'
      } transition-all duration-300 ease-in-out`}
    >
      <Navbar
        username={username}
        userStatus={userStatus}
        darkMode={darkMode}
        setDarkMode={setDarkMode}
        activeComponent={activeComponent}
        setActiveComponent={setActiveComponent}
        handleLogout={handleLogout}
      />

      <main className="flex-grow overflow-y-auto pb-20">
        <div className="container mx-auto px-4 py-8">
          {renderComponent()}
        </div>
      </main>

      <footer className="fixed bottom-0 left-0 right-0 bg-white dark:bg-gray-800 shadow-lg">
        <div className="container mx-auto px-4">
          <div className="flex justify-around py-4">
            {icons.map(({ key, label, icon: Icon }) => (
              <button
                key={key}
                onClick={() => setActiveComponent(key)}
                className={`flex flex-col items-center p-2 rounded-lg transition-colors duration-300 ${
                  activeComponent === key
                    ? 'bg-blue-600 text-white'
                    : darkMode
                    ? 'text-gray-400 hover:bg-gray-700 hover:text-white'
                    : 'text-gray-600 hover:bg-gray-200 hover:text-blue-600'
                }`}
              >
                <Icon className="text-2xl mb-1" />
                <span className="text-xs">{label}</span>
              </button>
            ))}
          </div>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
