import React, { useState } from 'react';
import {
  FiUser,
  FiPhone,
  FiVideo,
  FiGlobe,
  FiHelpCircle,
} from 'react-icons/fi';
import AccountSettings from './Settings/AccountSettings';
import CallSettings from './Settings/CallSettings';
import VideoSettings from './Settings/VideoSettings';
import LanguageRegion from './Settings/LanguageRegion';
import HelpSupport from './Settings/HelpSupport';

const Settings = ({ darkMode }) => {
  const [activeSetting, setActiveSetting] = useState('Account Settings');

  const settingsOptions = [
    { icon: <FiUser />, label: 'Account Settings' },
    { icon: <FiPhone />, label: 'Call Settings' },
    { icon: <FiVideo />, label: 'Video Settings' },
    { icon: <FiGlobe />, label: 'Language and Region' },
    { icon: <FiHelpCircle />, label: 'Help and Support' },
  ];

  const renderActiveSetting = () => {
    switch (activeSetting) {
      case 'Account Settings':
        return <AccountSettings darkMode={darkMode} />;
      case 'Call Settings':
        return <CallSettings darkMode={darkMode} />;
      case 'Video Settings':
        return <VideoSettings darkMode={darkMode} />;
      case 'Language and Region':
        return <LanguageRegion darkMode={darkMode} />;
      case 'Help and Support':
        return <HelpSupport darkMode={darkMode} />;
      default:
        return null;
    }
  };

  return (
    <div className={`max-w-5xl mx-auto ${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'} shadow-lg rounded-xl p-6 transition-all duration-300`}>
      <div className="flex flex-col md:flex-row md:space-x-8">
        <ul className="space-y-2 mb-6 md:mb-0 md:w-1/4">
          {settingsOptions.map((option, index) => (
            <li
              key={index}
              className={`flex items-center space-x-4 py-3 px-4 rounded-lg cursor-pointer transition duration-200 ${
                darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'
              } ${activeSetting === option.label ? (darkMode ? 'bg-gray-700' : 'bg-gray-100') : ''}`}
              onClick={() => setActiveSetting(option.label)}
            >
              <div className={`text-2xl ${darkMode ? 'text-blue-400' : 'text-blue-500'}`}>{option.icon}</div>
              <span className={`text-sm font-medium ${darkMode ? 'text-gray-200' : 'text-gray-700'}`}>{option.label}</span>
            </li>
          ))}
        </ul>
        <div className="flex-1 bg-gray-50 dark:bg-gray-700 p-6 rounded-lg shadow-inner">
          {renderActiveSetting()}
        </div>
      </div>
    </div>
  );
};

export default Settings;
