import React, { useState } from 'react';
import { FiPhone } from 'react-icons/fi';

const DialpadPage = () => {
  const [number, setNumber] = useState('');

  const handleNumberClick = (digit) => {
    setNumber(prevNumber => prevNumber + digit);
  };

  const handleCall = () => {
    alert(`Calling ${number}`);
    setNumber('');
  };

  const handleBackspace = () => {
    setNumber(prevNumber => prevNumber.slice(0, -1));
  };

  const dialpadButtons = [
    '1', '2', '3',
    '4', '5', '6',
    '7', '8', '9',
    '*', '0', '#'
  ];

  return (
    <div className="container mx-auto px-4 py-8 max-w-md">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
        <input
          type="text"
          value={number}
          readOnly
          className="w-full text-3xl text-center mb-6 p-2 bg-gray-100 dark:bg-gray-700 rounded-md"
        />
        <div className="grid grid-cols-3 gap-4">
          {dialpadButtons.map((button) => (
            <button
              key={button}
              onClick={() => handleNumberClick(button)}
              className="text-2xl font-semibold p-4 bg-gray-200 dark:bg-gray-600 rounded-full hover:bg-gray-300 dark:hover:bg-gray-500 transition-colors duration-300"
            >
              {button}
            </button>
          ))}
        </div>
        <div className="flex justify-between mt-6">
          <button
            onClick={handleBackspace}
            className="text-xl font-semibold p-4 bg-red-500 text-white rounded-full hover:bg-red-600 transition-colors duration-300"
          >
            ‚Üê
          </button>
          <button
            onClick={handleCall}
            className="text-xl font-semibold p-4 bg-green-500 text-white rounded-full hover:bg-green-600 transition-colors duration-300"
          >
            <FiPhone />
          </button>
        </div>
      </div>
    </div>
  );
};

export default DialpadPage;
