import React, { useState } from 'react';
import { Phone, Delete } from 'lucide-react';

const Dialpad = () => {
  const [number, setNumber] = useState('');

  const handleNumberClick = (digit) => {
    setNumber((prevNumber) => prevNumber + digit);
  };

  const handleDelete = () => {
    setNumber((prevNumber) => prevNumber.slice(0, -1));
  };

  const handleCall = () => {
    alert(`Calling ${number}`);
    setNumber('');
  };

  const buttons = ['1', '2', '3', '4', '5', '6', '7', '8', '9', '*', '0', '#'];

  return (
    <div className="flex flex-col items-center space-y-4 min-h-screen p-2 bg-gray-50">
      {/* Display Screen */}
      <div className="w-48 bg-gray-100 border border-gray-300 rounded-md shadow-sm">
        <input
          type="text"
          className="w-full text-lg text-gray-800 font-mono text-center bg-transparent focus:outline-none p-2"
          value={number}
          readOnly
          placeholder="Enter number"
        />
      </div>

      {/* Dial Pad */}
      <div className="grid grid-cols-3 gap-1">
        {buttons.map((btn) => (
          <button
            key={btn}
            className="w-10 h-10 bg-blue-500 text-white rounded-full shadow-md hover:bg-blue-600 active:scale-95 text-base flex items-center justify-center transition"
            onClick={() => handleNumberClick(btn)}
          >
            {btn}
          </button>
        ))}
      </div>

      {/* Action Buttons */}
      <div className="flex justify-between w-48 space-x-2">
        <button
          className="flex-1 flex items-center justify-center bg-red-500 hover:bg-red-600 text-white font-medium py-1 rounded-md shadow-sm text-sm transition"
          onClick={handleDelete}
        >
          <Delete size={16} className="mr-1" />
          Erase
        </button>
        <button
          className="flex-1 flex items-center justify-center bg-green-500 hover:bg-green-600 text-white font-medium py-1 rounded-md shadow-sm text-sm transition"
          onClick={handleCall}
        >
          <Phone size={16} className="mr-1" />
          Call
        </button>
      </div>
    </div>
  );
};

export default Dialpad;
