import React, { useState } from 'react';
import { UserIcon, PhoneIcon, VideoCameraIcon, GlobeAltIcon, QuestionMarkCircleIcon } from '@heroicons/react/24/outline';
import AccountSettings from './Settings/AccountSettings';
import CallSettings from './Settings/CallSettings';
import VideoSettings from './Settings/VideoSettings';
import LanguageRegion from './Settings/LanguageRegion';
import HelpSupport from './Settings/HelpSupport';

const Settings = ({ darkMode }) => {
  const [activeSetting, setActiveSetting] = useState('Account Settings');

  const settingsOptions = [
    { icon: UserIcon, label: 'Account Settings' },
    { icon: PhoneIcon, label: 'Call Settings' },
    { icon: VideoCameraIcon, label: 'Video Settings' },
    { icon: GlobeAltIcon, label: 'Language and Region' },
    { icon: QuestionMarkCircleIcon, label: 'Help and Support' },
  ];

  const renderActiveSetting = () => {
    switch (activeSetting) {
      case 'Account Settings':
        return <AccountSettings darkMode={darkMode} />;
      case 'Call Settings':
        return <CallSettings darkMode={darkMode} />;
      case 'Video Settings':
        return <VideoSettings darkMode={darkMode} />;
      case 'Language and Region':
        return <LanguageRegion darkMode={darkMode} />;
      case 'Help and Support':
        return <HelpSupport darkMode={darkMode} />;
      default:
        return null;
    }
  };

  return (
    <div className={`max-w-5xl mx-auto ${darkMode ? 'dark' : ''}`}>
      <div className={`bg-white dark:bg-gray-800 shadow-lg rounded-xl p-6 transition-all duration-300`}>
        <div className="flex flex-col md:flex-row">
          <ul className="space-y-2 mb-6 md:mb-0 md:mr-6 md:w-1/4">
            {settingsOptions.map((option, index) => (
              <li
                key={index}
                className={`flex items-center space-x-3 py-3 px-4 rounded-lg cursor-pointer transition duration-200 ${
                  darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'
                } ${activeSetting === option.label ? (darkMode ? 'bg-gray-700' : 'bg-gray-100') : ''}`}
                onClick={() => setActiveSetting(option.label)}
              >
                <option.icon className={`h-6 w-6 ${darkMode ? 'text-blue-400' : 'text-blue-500'}`} />
                <span className={`text-sm font-medium ${darkMode ? 'text-white' : 'text-gray-700'}`}>{option.label}</span>
              </li>
            ))}
          </ul>
          <div className="flex-1 bg-gray-50 dark:bg-gray-700 p-6 rounded-lg">
            {renderActiveSetting()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
