import React, { useState, useEffect } from 'react';
import { PhoneIcon, ClockIcon, Cog6ToothIcon, BellIcon, MoonIcon, SunIcon, ArrowLeftIcon, HomeIcon } from '@heroicons/react/24/outline';
import Navbar from '../components/Navbar';
import CallLog from '../components/CallLog';
import Settings from '.../components/Settings';
import NewUI from './components/NewUI';

const HomePage = () => {
  const [activeComponent, setActiveComponent] = useState('welcome');
  const [darkMode, setDarkMode] = useState(false);
  const username = 'Keeerthy Suresh';
  const userStatus = 'active';

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const renderComponent = () => {
    switch (activeComponent) {
      case 'call':
        return <NewUI darkMode={darkMode} />;
      case 'call-log':
        return <CallLog darkMode={darkMode} />;
      case 'settings':
        return <Settings darkMode={darkMode} />;
      default:
        return (
          <div className="text-center mt-16 space-y-6 px-4">
            <h1 className="text-4xl font-bold text-gray-800 dark:text-white">Welcome to Echo Link</h1>
            <p className="text-lg text-gray-600 dark:text-gray-300">Click an icon to view a section.</p>
            <div className="grid grid-cols-2 gap-4 max-w-md mx-auto mt-8">
              {icons.map(({ key, label, icon: Icon }) => (
                <button
                  key={key}
                  onClick={() => setActiveComponent(key)}
                  className="flex flex-col items-center justify-center p-6 bg-white dark:bg-gray-800 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 space-y-2"
                >
                  <Icon className="h-12 w-12 text-blue-500" />
                  <span className="text-sm font-medium text-gray-700 dark:text-gray-200">{label}</span>
                </button>
              ))}
            </div>
          </div>
        );
    }
  };

  const icons = [
    { key: 'call', label: 'Call', icon: PhoneIcon },
    { key: 'call-log', label: 'Call Log', icon: ClockIcon },
    { key: 'settings', label: 'Settings', icon: Cog6ToothIcon },
  ];

  const handleLogout = () => {
    // Add logout logic here
    window.location.href = '/';
  };

  return (
    <div className={`min-h-screen flex flex-col bg-gray-100 dark:bg-gray-900 transition-all duration-300 ease-in-out`}>
      <Navbar
        username={username}
        userStatus={userStatus}
        darkMode={darkMode}
        setDarkMode={setDarkMode}
        activeComponent={activeComponent}
        setActiveComponent={setActiveComponent}
        handleLogout={handleLogout}
      />

      <main className="flex-grow overflow-y-auto pb-20">
        <div className="container mx-auto px-4 py-8">
          {renderComponent()}
        </div>
      </main>

      <footer className="fixed bottom-0 left-0 right-0 bg-white dark:bg-gray-800 shadow-lg py-2">
        <div className="container mx-auto px-4">
          <div className="flex justify-around items-center">
            {icons.map(({ key, label, icon: Icon }) => (
              <button
                key={key}
                onClick={() => setActiveComponent(key)}
                className={`flex flex-col items-center py-2 px-4 rounded-lg transition-colors duration-300 ${
                  activeComponent === key
                    ? 'text-blue-600 dark:text-blue-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-blue-600 dark:hover:text-blue-400'
                }`}
              >
                <Icon className="h-6 w-6 mb-1" />
                <span className="text-xs">{label}</span>
              </button>
            ))}
          </div>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
