import React from 'react';
import { MoonIcon, SunIcon, ArrowLeftIcon, HomeIcon, BellIcon } from '@heroicons/react/24/outline';

const Navbar = ({ username, userStatus, darkMode, setDarkMode, activeComponent, setActiveComponent }) => {
  return (
    <nav
      className={`w-full px-4 sm:px-6 py-4 shadow-lg ${
        darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'
      } transition-all duration-300 ease-in-out sticky top-0 z-10`}
    >
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <span className={`text-xl sm:text-2xl font-extrabold tracking-wider ${darkMode ? 'text-blue-400' : 'text-blue-600'}`}>
            ECHO-LINK
          </span>
        </div>

        <div className="flex items-center space-x-4 sm:space-x-6">
          <button
            onClick={() => setActiveComponent('welcome')}
            className={`flex items-center text-sm sm:text-base font-medium px-3 py-2 rounded-lg transition-colors duration-300 ${
              darkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-200 hover:bg-gray-300'
            }`}
          >
            {activeComponent !== 'welcome' ? <ArrowLeftIcon className="h-5 w-5 mr-2" /> : <HomeIcon className="h-5 w-5 mr-2" />}
            {activeComponent !== 'welcome' ? 'Back' : 'Home'}
          </button>

          <button
            className={`relative p-2 rounded-full transition-colors duration-300 ${
              darkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-200 hover:bg-gray-300'
            }`}
            aria-label="Notifications"
          >
            <BellIcon className="h-6 w-6" />
            <span className="absolute top-0 right-0 bg-red-500 text-white text-xs font-bold w-4 h-4 flex items-center justify-center rounded-full">
              3
            </span>
          </button>

          <div className="flex items-center space-x-2">
            <span
              className={`w-3 h-3 rounded-full ${
                userStatus === 'active' ? 'bg-green-500' : 'bg-red-500'
              }`}
              title={userStatus === 'active' ? 'Active' : 'Inactive'}
            ></span>
            <div className="hidden sm:block">
              <p className="text-sm font-medium">{username}</p>
              <p className={`text-xs ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>Status: {userStatus}</p>
            </div>
          </div>

          <button
            onClick={() => setDarkMode(!darkMode)}
            className={`p-2 rounded-full transition-colors duration-300 ${
              darkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-200 hover:bg-gray-300'
            }`}
            aria-label={darkMode ? 'Switch to light mode' : 'Switch to dark mode'}
          >
            {darkMode ? <SunIcon className="h-6 w-6" /> : <MoonIcon className="h-6 w-6" />}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
